# $Key need to be set
function DeShuffle ([char[]]$bar,[char]$key){($bar|Get-Random -Count $bar.Count -SetSeed $key)-join''}
function DeCode ([string]$T){try{[regex]::Replace($T,'#..', {param($baz)[char][int](($baz.value).replace('#','0x'))})}catch{Write-Warning "Bad Password ?"} }
function DeCrypt ($E){[char[]]$d=$Key;[array]::Reverse($d);foreach($c in $d){$E=Deshuffle $E $c};DeCode $E}
